import React, { useState } from 'react';
import './Header.css';
import { Link } from 'react-scroll'

interface HeaderProps {
  cambiarIdioma: () => void; 

  texto: Header;
  
}
interface Header {
  cambiarIdi: string;
  aboutme: string;
  contact: string;
  project: string;
}

const Header: React.FC<HeaderProps> = ({ cambiarIdioma, texto }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };
  return (
    <header className='Header'>
      <div className='language-switch'>
        <button onClick={cambiarIdioma}>{texto.cambiarIdi}</button>
        
      </div>
      <nav className="NavigationMenu">
      <div className="toggleButton" onClick={toggleMenu}>
        â˜°
      </div>
      <ul className={`menuItems ${isOpen ? 'open' : ''}`}>
        <li><Link to="aboutme" spy={true} smooth={true} duration={500} onClick={toggleMenu}>{texto.aboutme}</Link></li>
        <li><Link to="projects" spy={true} smooth={true} duration={500} onClick={toggleMenu}>{texto.project}</Link></li>
        <li><Link to="contact" spy={true} smooth={true} duration={500} onClick={toggleMenu}>{texto.contact}</Link></li>
      </ul>
    </nav>
    </header>
  );
};

export default Header;
