import React, { useState } from 'react';
import {link} from 'react-scroll';
import './NavigationMenu.css';


const NavigationMenu: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const scrollToSection = (id: string) => {
    const section = document.getElementById(id);
    if (section) {
      window.scrollTo({
        behavior: 'smooth',
        top: section.offsetTop
      });
    }
    setIsOpen(false);
  };

  return (
    <nav className="NavigationMenu">
      <div className="toggleButton" onClick={toggleMenu}>
        â˜°
      </div>
      <ul className={`menuItems ${isOpen ? 'open' : ''}`}>
        <li><button onClick={() => scrollToSection('aboutme')}>Acerca de</button></li>
        <li><button onClick={() => scrollToSection('projects')}>Proyectos</button></li>
        <li><button onClick={() => scrollToSection('contact')}>Contacto</button></li>
      </ul>
    </nav>
  );
};

export default NavigationMenu;
